CREATE TABLE OwnerCompany (
    CompanyID INT PRIMARY KEY,
    Name VARCHAR(255),
    Headquarters VARCHAR(255),
    FoundedYear INT,
    CEO VARCHAR(255),
    Revenue DECIMAL(15, 2),
    Employees INT,
    Industry VARCHAR(50),
    Website VARCHAR(255),
    ContactEmail VARCHAR(255)
);

CREATE TABLE PowerPlant (
    PlantID INT PRIMARY KEY,
    Name VARCHAR(255),
    Location VARCHAR(255),
    CapacityMW DECIMAL(10, 2),
    Type VARCHAR(50),
    CommissionDate DATE,
    OwnerCompanyID INT,
    Status VARCHAR(50),
    Latitude DECIMAL(9, 6),
    Longitude DECIMAL(9, 6),
    FOREIGN KEY (OwnerCompanyID) REFERENCES OwnerCompany(CompanyID)
);

CREATE TABLE EnergySource (
    SourceID INT PRIMARY KEY,
    Name VARCHAR(255),
    Type VARCHAR(50),
    Description TEXT,
    Renewable BOOLEAN,
    EmissionFactor DECIMAL(5, 2),
    CostPerUnit DECIMAL(10, 2),
    Availability VARCHAR(50),
    CountryOfOrigin VARCHAR(255),
    Efficiency DECIMAL(5, 2)
);

CREATE TABLE Supplier (
    SupplierID INT PRIMARY KEY,
    SourceID INT,
    Name VARCHAR(255),
    Country VARCHAR(255),
    ContactName VARCHAR(255),
    ContactPhone VARCHAR(20),
    ContactEmail VARCHAR(255),
    EstablishedYear INT,
    ProductRange TEXT,
    AnnualSupplyVolume DECIMAL(15, 2),
    Rating DECIMAL(3, 2),
    FOREIGN KEY (SourceID) REFERENCES EnergySource(SourceID)
);

CREATE TABLE TransmissionLine (
    LineID INT PRIMARY KEY,
    Name VARCHAR(255),
    VoltageLevel VARCHAR(50),
    LengthKM DECIMAL(10, 2),
    StartLocation VARCHAR(255),
    EndLocation VARCHAR(255),
    CommissionDate DATE,
    OwnerCompanyID INT,
    CapacityMW DECIMAL(10, 2),
    Status VARCHAR(50),
    FOREIGN KEY (OwnerCompanyID) REFERENCES OwnerCompany(CompanyID)
);

CREATE TABLE Substation (
    SubstationID INT PRIMARY KEY,
    Name VARCHAR(255),
    Location VARCHAR(255),
    CapacityMW DECIMAL(10, 2),
    VoltageLevel INT,
    CommissionDate DATE,
    OwnerCompanyID INT,
    Status VARCHAR(50),
    Latitude DECIMAL(9, 6),
    Longitude DECIMAL(9, 6),
    FOREIGN KEY (OwnerCompanyID) REFERENCES OwnerCompany(CompanyID)
);

CREATE TABLE Consumer (
    ConsumerID INT PRIMARY KEY,
    Name VARCHAR(255),
    Type VARCHAR(50),
    Location VARCHAR(255),
    ContactName VARCHAR(255),
    ContactPhone VARCHAR(20),
    ContactEmail VARCHAR(255),
    ConsumptionPattern TEXT,
    AverageMonthlyConsumption DECIMAL(10, 2)
);

CREATE TABLE `Contract` (
    ContractID INT PRIMARY KEY,
    ConsumerID INT,
    SupplierID INT,
    StartDate DATE,
    EndDate DATE,
    Terms TEXT,
    PricePerUnit DECIMAL(10, 2),
    Volume DECIMAL(15, 2),
    Status VARCHAR(50),
    RenewalOption BOOLEAN,
    FOREIGN KEY (SupplierID) REFERENCES Supplier(SupplierID),
    FOREIGN KEY (ConsumerID) REFERENCES Consumer(ConsumerID)
);

CREATE TABLE Meter (
    MeterID INT PRIMARY KEY,
    ConsumerID INT,
    InstallationDate DATE,
    Type VARCHAR(50),
    LastReading DECIMAL(10, 2),
    LastReadingDate DATE,
    Status VARCHAR(50),
    Manufacturer VARCHAR(255),
    Model VARCHAR(255),
    CalibrationDate DATE,
    FOREIGN KEY (ConsumerID) REFERENCES Consumer(ConsumerID)
);

CREATE TABLE Tariff (
    TariffID INT PRIMARY KEY,
    Name VARCHAR(255),
    Description TEXT,
    PricePerUnit DECIMAL(10, 2),
    ValidFrom DATE,
    ValidTo DATE,
    ConsumerType VARCHAR(50),
    PeakHours TEXT,
    OffPeakHours TEXT,
    Status VARCHAR(50)
);

CREATE TABLE Emission (
    EmissionID INT PRIMARY KEY,
    PlantID INT,
    Date DATE,
    CO2Emissions DECIMAL(10, 2),
    SO2Emissions DECIMAL(10, 2),
    NOxEmissions DECIMAL(10, 2),
    ParticulateMatter DECIMAL(10, 2),
    ReportingPeriod VARCHAR(50),
    ComplianceStatus VARCHAR(50),
    Notes TEXT,
    FOREIGN KEY (PlantID) REFERENCES PowerPlant(PlantID)
);

CREATE TABLE Maintenance (
    MaintenanceID INT PRIMARY KEY,
    PlantID INT,
    Date DATE,
    Description TEXT,
    DurationHours DECIMAL(5, 2),
    Cost DECIMAL(10, 2),
    PerformedBy VARCHAR(255),
    Status VARCHAR(50),
    NextScheduledDate DATE,
    Notes TEXT,
    FOREIGN KEY (PlantID) REFERENCES PowerPlant(PlantID)
);

CREATE TABLE Incident (
    IncidentID INT PRIMARY KEY,
    PlantID INT,
    Date DATE,
    Description TEXT,
    Severity VARCHAR(50),
    Impact VARCHAR(50),
    Resolution TEXT,
    DurationHours DECIMAL(5, 2),
    ReportedBy VARCHAR(255),
    Status VARCHAR(50),
    FOREIGN KEY (PlantID) REFERENCES PowerPlant(PlantID)
);

CREATE TABLE Regulation (
    RegulationID INT PRIMARY KEY,
    Name VARCHAR(255),
    Description TEXT,
    EffectiveDate DATE,
    ExpiryDate DATE,
    Authority VARCHAR(255),
    ComplianceRequirements TEXT,
    Penalties TEXT,
    Status VARCHAR(50),
    Notes TEXT
);

CREATE TABLE `Audit` (
    AuditID INT PRIMARY KEY,
    PlantID INT,
    Date DATE,
    Auditor VARCHAR(255),
    Findings TEXT,
    Recommendations TEXT,
    ComplianceStatus VARCHAR(50),
    FollowUpDate DATE,
    Report VARCHAR(255),
    Notes TEXT,
    FOREIGN KEY (PlantID) REFERENCES PowerPlant(PlantID)
);






























