CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    first_name VARCHAR(100),
    last_name VARCHAR(100),
    birth_date DATE,
    gender VARCHAR(10),
    nationality VARCHAR(50),
    email VARCHAR(100),
    phone_number VARCHAR(20),
    position VARCHAR(100)
);

CREATE TABLE Institutions (
    institution_id INT PRIMARY KEY,
    name VARCHAR(255),
    type VARCHAR(100),
    country VARCHAR(100),
    city VARCHAR(100),
    address VARCHAR(255),
    phone_number VARCHAR(20),
    email VARCHAR(100),
    website VARCHAR(100),
    established_year INT
);

CREATE TABLE Departments (
    department_id INT PRIMARY KEY,
    head_id INT,
    name VARCHAR(255),
    institution_id INT,
    phone_number VARCHAR(20),
    email VARCHAR(100),
    building VARCHAR(100),
    floor INT,
    number_of_staff INT,
    budget DECIMAL(15, 2),
    FOREIGN KEY (institution_id) REFERENCES Institutions(institution_id),
    FOREIGN KEY (head_id) REFERENCES Researchers(researcher_id)
);

CREATE TABLE Projects (
    project_id INT PRIMARY KEY,
    title VARCHAR(255),
    start_date DATE,
    end_date DATE,
    budget DECIMAL(15, 2),
    funding_agency VARCHAR(255),
    project_lead_id INT,
    department_id INT,
    status VARCHAR(50),
    FOREIGN KEY (department_id) REFERENCES Departments(department_id),
    FOREIGN KEY (project_lead_id) REFERENCES Researchers(researcher_id)
);

CREATE TABLE Publications (
    publication_id INT PRIMARY KEY,
    title VARCHAR(255),
    publication_date DATE,
    journal VARCHAR(255),
    volume INT,
    issue INT,
    pages VARCHAR(50),
    doi VARCHAR(100),
    impact_factor DECIMAL(3, 2),
    project_id INT,
    FOREIGN KEY (project_id) REFERENCES Projects(project_id)
);

CREATE TABLE Conferences (
    conference_id INT PRIMARY KEY,
    name VARCHAR(255),
    start_date DATE,
    end_date DATE,
    location VARCHAR(255),
    organizer VARCHAR(255),
    website VARCHAR(100),
    contact_email VARCHAR(100),
    contact_phone VARCHAR(20),
    number_of_participants INT
);

CREATE TABLE Presentations (
    presentation_id INT PRIMARY KEY,
    title VARCHAR(255),
    conference_id INT,
    presenter_id INT,
    date DATE,
    time TIME,
    duration INT,
    room VARCHAR(100),
    abstract TEXT,
    slides_url VARCHAR(255),
    project_id INT,
    FOREIGN KEY (project_id) REFERENCES Projects(project_id),
    FOREIGN KEY (conference_id) REFERENCES Conferences(conference_id),
    FOREIGN KEY (presenter_id) REFERENCES Researchers(researcher_id)
);

CREATE TABLE Patents (
    patent_id INT PRIMARY KEY,
    title VARCHAR(255),
    application_date DATE,
    grant_date DATE,
    patent_number VARCHAR(100),
    inventor_id INT,
    project_id INT,
    status VARCHAR(50),
    country VARCHAR(100),
    FOREIGN KEY (project_id) REFERENCES Projects(project_id),
    FOREIGN KEY (inventor_id) REFERENCES Researchers(researcher_id)
);

CREATE TABLE Collaborations (
    collaboration_id INT PRIMARY KEY,
    project_id INT,
    institution_id INT,
    start_date DATE,
    end_date DATE,
    type VARCHAR(100),
    contact_person VARCHAR(100),
    contact_email VARCHAR(100),
    contact_phone VARCHAR(20),
    description TEXT,
    FOREIGN KEY (institution_id) REFERENCES Institutions(institution_id),
    FOREIGN KEY (project_id) REFERENCES Projects(project_id)
);

CREATE TABLE Grants (
    grant_id INT PRIMARY KEY,
    title VARCHAR(255),
    amount DECIMAL(15, 2),
    funding_agency VARCHAR(255),
    start_date DATE,
    end_date DATE,
    project_id INT,
    status VARCHAR(50),
    purpose TEXT,
    FOREIGN KEY (project_id) REFERENCES Projects(project_id)
);

CREATE TABLE Equipment (
    equipment_id INT PRIMARY KEY,
    name VARCHAR(255),
    model VARCHAR(100),
    manufacturer VARCHAR(100),
    purchase_date DATE,
    cost DECIMAL(15, 2),
    location VARCHAR(255),
    department_id INT,
    status VARCHAR(50),
    maintenance_schedule TEXT,
    FOREIGN KEY (department_id) REFERENCES Departments(department_id)
);

CREATE TABLE Experiments (
    experiment_id INT PRIMARY KEY,
    title VARCHAR(255),
    project_id INT,
    start_date DATE,
    end_date DATE,
    lead_researcher_id INT,
    description TEXT,
    results TEXT,
    status VARCHAR(50),
    equipment_id INT,
    location VARCHAR(255),
    FOREIGN KEY (lead_researcher_id) REFERENCES Researchers(researcher_id),
    FOREIGN KEY (project_id) REFERENCES Projects(project_id),
    FOREIGN KEY (equipment_id) REFERENCES Equipment(equipment_id)
);

CREATE TABLE Samples (
    sample_id INT PRIMARY KEY,
    experiment_id INT,
    type VARCHAR(100),
    collection_date DATE,
    storage_location VARCHAR(255),
    quantity DECIMAL(10, 2),
    unit VARCHAR(50),
    `condition` VARCHAR(100),
    `description` TEXT,
    `status` VARCHAR(50),
    FOREIGN KEY (experiment_id) REFERENCES Experiments(experiment_id)
);

CREATE TABLE DataSets (
    dataset_id INT PRIMARY KEY,
    title VARCHAR(255),
    project_id INT,
    creation_date DATE,
    format VARCHAR(50),
    size DECIMAL(10, 2),
    access_level VARCHAR(50),
    description TEXT,
    storage_location VARCHAR(255),
    status VARCHAR(50),
    FOREIGN KEY (project_id) REFERENCES Projects(project_id)
);

CREATE TABLE EthicsApprovals (
    approval_id INT PRIMARY KEY,
    project_id INT,
    application_date DATE,
    approval_date DATE,
    status VARCHAR(50),
    committee_name VARCHAR(255),
    contact_person VARCHAR(100),
    contact_email VARCHAR(100),
    contact_phone VARCHAR(20),
    conditions TEXT,
    FOREIGN KEY (project_id) REFERENCES Projects(project_id)
);






























